<?php/** * File Type: Locations Shortcode */ if (!function_exists('cs_locations_listing')) {    function cs_locations_listing( $atts, $content = "") {        global $post, $wpdb, $cs_node, $cs_theme_option, $excerpt, $view, $category;        $defaults = array('column_size' => '1/1','section_title'=>'','view'=>'','cs_vehicle_location_style'=>'','vehicle_type'=>'','order'=>'DESC','orderby'=>'ID','excerpt'=>'255','locations_pagination'=>'pagination','pagination'=>'10','filterable'=>'yes');        extract(shortcode_atts($defaults, $atts));		$coloumn_class = cs_custom_column_class($column_size);        $cs_dataObject = get_post_meta($post->ID, 'cs_full_data');   		ob_start();        $meta_compare = "";        $order 		  = 'DESC';        $orderby 	  = 'meta_value';        if (empty($_GET['page_id_all'])) {            $_GET['page_id_all'] = 1;		}        $args = array(                'posts_per_page' 	=> "-1",                'post_type' 		=> 'locations',                'post_status' 		=> 'publish',                'orderby' 			=> $orderby,                'order' 			=> $order,            );        $custom_query = new WP_Query($args);        $count_post = 0;        $counter 	= 1;        $count_post = $custom_query->post_count;        if ($pagination == '')            $pagination = '-1';       $args = array(                'posts_per_page' => "$pagination",                'paged' => $_GET['page_id_all'],                'post_type' => 'locations',                'post_status' => 'publish',                'orderby' => $orderby,                'order' => $order,            );				if( isset( $section_title ) && $section_title !='' ){			echo '<div class="element-size-100">                    <div class="col-md-12">                      <div class="cs-section-title">                        <h2>'.$section_title.'</h2>						                      </div>                    </div>                  </div>';		}        echo '<div class="locations-listing-wrapper">';	   	   if(isset($cs_vehicle_location_style) and $cs_vehicle_location_style == "default"){			include( 'locations-default.php' );	   } else {			include( 'locations-listing.php' );	   }		//==Pagination Start			 	if ( $count_post > $pagination && $pagination > 0 && $locations_pagination == 'pagination' ) {		 			$qrystr = '';			if (isset($_GET['page_id']))				$qrystr .= "&amp;page_id=" . $_GET['page_id'];			 echo cs_pagination($count_post, $pagination, $qrystr, 'Show Pagination');		}        //==Pagination End          		echo '</div>';        		$data = ob_get_clean();        return $data;   } add_shortcode('cs_locations', 'cs_locations_listing'); }