<?php/*** @Start notification helper class* @return*/if( !class_exists( 'CS_Plugin_Notification_Helper' ) ) {	class CS_Plugin_Notification_Helper{				public $message;				public function __construct() {			// Do Something here..		}				public function success( $message = 'Found' ) {			global $post;						$output	 = '';			$output	.= '<div class="col-md-12 cs-succ_mess"><p>';			$output	.= $message;			$output	.= '</p></div>';						echo force_balance_tags ( $output ); 				}				public function error( $message='No recored found' ) {			global $post;		   		   $output	 = '';		   $output	.= '<div class="alert alert-danger alert-dismissible" role="alert">						  <button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true"> <i class="icon-times-circle"></i></span></button>						  <i class="icon-warning2"></i> '.$message.'						</div>';		  		   echo force_balance_tags ( $output ); 				}	  		public function warning( $message='No recored found' ) {		  global $post;						  $output	 = '';		  $output	.= '<div class="col-md-12 cs-warning"><p>';		  $output	.= $message;		  $output	.= '</p></div>';		 		 echo force_balance_tags ( $output ); 		}				public function informations($message='No recored found') { 		  global $post;		 		  $output	 = '';		  $output	.= '<div class="col-md-12 cs-informations"><p>';		  $output	.= $message;		  $output	.= '</p></div>';		 		 echo force_balance_tags ( $output ); 				}	}		$cs_plugin_notify = new CS_Plugin_Notification_Helper();	global $cs_plugin_notify;}?>